def simpleCompute(computefunc, state0, t0 = 0, t_bound = 200, max_step = 0.01):
    def resultSC(model):
        return computefunc(
          model, state0 = state0, t0 = t0, t_bound = t_bound, max_step = max_step)
    return resultSC

